import React, { useState, useContext, useEffect } from "react";
import { AccordionHeading, AccordionBody } from "../accordion";
import { setItemInLocalStorage, formatSiteName } from "../../../../utilities.js";

import Pagination from "./../reuseable-components/pagination/pagination.js"

import Switches from "../../libs/use-switches";

export default function EventCustomerWasteVcustomerRefReport(props) {

  const {
    showEventCustomerWasteVcustomerRef,
    setShowEventCustomerWasteVcustomerRef,
  } = Switches()

  const changePageForWeeks = ({ selected }) => {
  };

  return (
    <div
    >
      <AccordionHeading color={"primPurpleBackgroundColor"}>
        <div className="section-title">EVENT CUSTOMER WASTE V CUSTOMER REF</div>
        <div className="container">
          <span
            onClick={() => {
              props.setShowEventCustomerWasteVcustomerRef(
                !props.showEventCustomerWasteVcustomerRef
              );
              setItemInLocalStorage(
                "eventCustomerWasteVcustomerRef",
                !props.showEventCustomerWasteVcustomerRef
              );
            }}
          >
            {props.showEventCustomerWasteVcustomerRef ? "X " : "| | | "}
          </span>
        </div>
      </AccordionHeading>

      <AccordionBody open={props.showEventCustomerWasteVcustomerRef}>
        <div className="originalTheme__reportBackgroundColor addPaddingOfHalveRem">

            {/* <SlackChart chartData={chartData} /> */}

            <div style={{
              display: "flex",
              padding: "2rem",
              fontSize: "10px"
            }}>
              <Pagination index={0} onPageChange={changePageForWeeks} pageCount={0} />
            </div>

        </div>
      </AccordionBody>
    </div>
  );
}
