import React, { useRef, useState } from "react";
import Form from "react-bootstrap/Form";
import { useHistory } from "react-router-dom";

import "./public-live-feed.css";
import { s3Upload } from "./file-uploader";

export default function PublicLiveFeed() {
  const file = useRef(null);
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);

  const [content, setContent] = useState("");
  const [surveyOneContent, setSurveyOneContent] = useState("");
  const [surveyTwoContent, setSurveyTwoContent] = useState("");
  const [surveyThreeContent, setSurveyThreeContent] = useState("");
  const [surveyFourContent, setSurveyFourContent] = useState("");


  function validateForm() {
    return content.length > 0;
  }

  function handleFileChange(event) {
    file.current = event.target.files[0];
  }

  async function handleSubmit(event) {
    event.preventDefault();

    if (file.current && file.current.size > 5000000) {
      alert(
        `Please pick a file smaller than ${
          5000000 / 1000000
        } MB.`
      );
      return;
    }

    setIsLoading(true);

    try {
      // console.log("file.current",file.current);
      // const attachment = file.current ? await s3Upload(file.current) : null;
      // console.log("attachment",attachment);

      const test = "1638021221938-fxplus_logo.jpeg"
      const note = Storage.vault.get(test);
      console.log("note",note);

      // await createNote({ content, attachment });
      // history.push("/");
    } catch (e) {
      // onError(e);
      setIsLoading(false);
    }
  }

  return (
    <div className="contentManagement" style={{
      width: "100%",
      height: "100%",
      paddingTop: "15rem"
    }}>
      <div style={{
            display: "flex",
            height: "100%"
      }}>
        <div style={{
          height: "100%",
          width: "100%",
          display: "flex",
          flexDirection: "column",
          // backgroundColor: "red"
        }}>

      <Form onSubmit={handleSubmit} className="contentManagement_form">
        <Form.Group controlId="content">
          <h2>Write your opening lines for the live feed below:</h2>
          <Form.Control
            value={content}
            as="textarea"
            onChange={(e) => setContent(e.target.value)}
          />
          <h2>Survey question 1</h2>
          <Form.Control
            value={content}
            as="textarea"
            onChange={(e) => setContent(e.target.value)}
          />
          <h2>Survey question 2</h2>
          <Form.Control
            value={content}
            as="textarea"
            onChange={(e) => setContent(e.target.value)}
          />
          <h2>Survey question 3</h2>
          <Form.Control
            value={content}
            as="textarea"
            onChange={(e) => setContent(e.target.value)}
          />
          <h2>Survey question 4</h2>
          <Form.Control
            value={content}
            as="textarea"
            onChange={(e) => setContent(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="file">
          <Form.Label>Attachment (add company logo for live feed)</Form.Label>
          <Form.Control onChange={handleFileChange} type="file" />
        </Form.Group>
        <button>
            <h3>Submit</h3>
        </button>
      </Form>

        </div>
        <div style={{
          height: "100%",
          width: "100%",
          display: "flex",
          flexDirection: "column",
          backgroundColor: "green"
        }}></div>
      </div>
      {/* <Form onSubmit={handleSubmit} className="contentManagement_form">
        <Form.Group controlId="content">
          <h2>Write your opening lines for the live feed below:</h2>
          <Form.Control
            value={content}
            as="textarea"
            onChange={(e) => setContent(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="file">
          <Form.Label>Attachment (add company logo for live feed)</Form.Label>
          <Form.Control onChange={handleFileChange} type="file" />
        </Form.Group>
            <button>
                <h3>Submit</h3>
            </button>
      </Form> */}
    </div>
  );
}
