import React from "react";

export const AccordionHeading = (props) => {
    return (
        <div className={`${props.group} ? ${props.color} : accordion__heading originalTheme__accordionHeading ${props.color}`}>
            {props.children}
        </div>
    )
};

export const AccordionBody = (props) => {
    return (
        <div className={(props.open ? "accordion__opening " : "displayNone accordion__closing")}>
            {props.children}
        </div>
    )
};
